//-----------------------------------------------------------------------------
// the state machine
//-----------------------------------------------------------------------------
//                         VinLow == False
// ┌────────────────────────────────────────────────────────────┐
// │                                                            │
// │                       VinLow Interrupt    ┌────────────┐   │
// │                  ┌───────────────────────►│            │   │
// │                  │                        │    VLow    ├───┘
// │   ┌────────────┐ │                     ┌─►│            │
// │   │            ├─┘                     │  └────────────┘
// ├──►│    Init    │                       │  nReset = False
// │   │            ├─┐     VinLow Interrupt│
// │   └────────────┘ │                     │
// │   nReset = False │                     │
// │                  │                     │  ┌────────────┐
// │                  │ VinLow == False     └──┤            │
// │                  │    1 second            │     OK     ├───┐
// │                  └───────────────────────►│            │   │
// │                                           └────────────┘   │
// │                                            nReset = True   │
// │                                           Modbus WDT SMEn  │
// │                 Modbus WDT Timeout                         │
// └────────────────────────────────────────────────────────────┘
// outputs
bool nReset = false; // inverse logic
bool modbusWdtSmEn = false;
// inputs
bool cprif = false; // set outside, cleared here
bool VinCmp = true; // set and cleared before calling
bool modbusWdtExp = false; // set outside, cleared here
uint8_t sCounter = 0;
#define SCOUNTER_ONE_SECOND (125)
// 125 for one second

typedef enum
{
	Init,
	VLow,
	OK
}
VinSm_t;

void VinSm (void)
{
	static VinSm_t VinState = Init;
	// default output states
	nReset = false;
	modbusWdtSmEn = false;

	// transitions
	switch (VinState)
	{
	case Init:
		if (cprif == true || VinCmp == true)
		{
			VinState = VLow;
		}
		else if (sCounter >= SCOUNTER_ONE_SECOND * 2 - 1)
		{
			VinState = OK;
		}
		break;
	case VLow:
		if (VinCmp == false)
		{
			VinState = Init;
		}
		break;
	case OK:
		if (cprif == true || VinCmp == true)
		{
			VinState = VLow;
		}
		else if (modbusWdtExp == true)
		{
			VinState = Init;
		}
		break;
	default:
		VinState = Init;
		break;
	}

	// outputs
	switch (VinState)
	{
	case Init:
		sCounter++;
		break;
	case VLow:
		sCounter = 0;
		break;
	case OK:
		nReset = true;
		modbusWdtSmEn = true;
		sCounter = 0;
		break;
	}

	cprif = false;
}

